import React, { useState , useEffect } from "react";
import styled from "styled-components/native";
import Weather from "../../components/Weather"; 
import { StyleSheet, ScrollView, SafeAreaView } from "react-native";
import Covid from "../../components/Covid";
import constants from "../../constants";
import { useQuery } from "react-apollo-hooks";
import { gql } from 'apollo-boost';

const Image = styled.Image`
  margin-top : -30px;
  margin-bottom : -30px;
  width: ${constants.width / 3};
`;

const View = styled.View`
  justify-content: center;
  align-items: center;
  flex: 1;
`;

const GET_WEATHER = gql`
  query weather($latitude: Float! , $longitude:Float!) {
    weather(latitude: $latitude, longitude:$longitude){
        temp 
        weather
        }
    }
`;

const GET_COVID = gql`
  query covid19($location:String!) {
    covid19(location:$location){
      countryName
      newCase
      }
    }
`;



export default ({ location }) => {

const [sliderState, setSliderState] = useState({ currentPage: 0 });
const { width, height } = constants.width;

  const setSliderPage = (event) => {
    const { currentPage } = sliderState;
    const { x } = event.nativeEvent.contentOffset;
    const indexOfNextScreen = Math.floor(x / width);
    if (indexOfNextScreen !== currentPage) {
      setSliderState({
        ...sliderState,
        currentPage: indexOfNextScreen,
      });
    }
  };

  const { currentPage: pageIndex } = sliderState;

  const { data: weatherData, loading: weatherLoding } = useQuery(GET_WEATHER, {
    variables: {
      latitude: location.latitude,
      longitude: location.longitude
    }
  });
  const { data: covidData, loading: covidLoading } = useQuery(GET_COVID, {
    variables: {
      location: location.region
    }
  });

  return (covidData && weatherLoding ? (<Image resizeMode={"contain"} source={require("../../assets/logo.png")} />)
    : (
    <>

          <View style={{ width, height }}>
            <Image style={styles.imageStyle} source={require("../../assets/logo.png")} />
            <View style={styles.wrapper}>
            </View>
          </View>
          <View style={{ width, height }}>
            <Covid countryName={covidData.covid19.countryName} newCase={covidData.covid19.newCase} />
            <View style={styles.wrapper}>
            </View>
          </View>
          <View style={{ width, height }}>
           <Weather weather={weatherData.weather.weather} temp={weatherData.weather.temp} />
            <View style={styles.wrapper}>
            </View>
          </View>
    </>
  )
  
  )
};

const styles = StyleSheet.create({
  imageStyle: {
    height: "10%",
    width: '80%',
  },
  wrapper: {
    justifyContent: 'center',
    alignItems: 'center',
    marginVertical: 30,
  },
  paginationWrapper: {
    position: 'absolute',
    bottom: 200,
    left: 0,
    right: 0,
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'row',
  },
  paginationDots: {
    height: 10,
    width: 10,
    borderRadius: 10 / 2,
    backgroundColor: '#0898A0',
    marginLeft: 10,
  },
});
