import React, { useState , useEffect } from "react";
import styled from "styled-components/native";
import Weather from "../../components/Weather"; 
import Covid from "../../components/Covid";
import constants from "../../constants";
import { StyleSheet } from "react-native";
import { useQuery } from "react-apollo-hooks";
import { gql } from 'apollo-boost';

const Image = styled.Image`
  transform: translateY(30px);
  margin-top : -30px;
  margin-bottom : -30px;
  width: ${constants.width / 3};
`;

const View = styled.View`
  justify-content: center;
  align-items: center;
  flex: 1;
`;

const GET_WEATHER = gql`
  query weather($latitude: Float! , $longitude:Float!) {
    weather(latitude: $latitude, longitude:$longitude){
        temp 
        weather
        }
    }
`;

const GET_COVID = gql`
  query covid19($location:String!) {
    covid19(location:$location){
      countryName
      newCase
      }
    }
`;



export default ({ location }) => {

  const { data: weatherData, loading: weatherLoding } = useQuery(GET_WEATHER, {
    variables: {
      latitude: location.latitude,
      longitude: location.longitude
    }
  });
  const { data: covidData, loading: covidLoading } = useQuery(GET_COVID, {
    variables: {
      location: location.region
    }
  });

  const saveViews = () => { 

  }

  return (covidData && weatherLoding ? (<Image resizeMode={"contain"} source={require("../../assets/logo.png")} />)
    : (<View styles={styles.container}>
      {/* <Image resizeMode={"contain"} source={require("../../assets/logo.png")} /> */}
      {<Covid countryName={covidData.covid19.countryName} newCase={covidData.covid19.newCase} />}
      {<Weather weather={weatherData.weather.weather} temp={weatherData.weather.temp} />}
    </View>)
  
  )
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    width: "150px",
    height: "50px",
    overflow:"hidden",
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center"
  },
  temp: {
    fontSize: 17,
    fontWeight:'bold',
    color: "black"
  }
});
